#!/bin/bash\n# üßπ SPACE GAME PROJECT CLEANUP SCRIPT\n# This script removes all duplicate and obsolete files\n\necho \"üöÄ SPACE GAME - Automated Cleanup Script\"\necho \"===========================================\"\necho \"\"\n\n# Create temporary directory for cleanup\necho \"üìÅ Creating temporary workspace...\"\nTEMP_DIR=\"spaceGame-cleanup-$(date +%s)\"\ngit clone -b action-02-balance-experiments https://github.com/ServH/spaceGame.git \"$TEMP_DIR\"\n\nif [ $? -ne 0 ]; then\n    echo \"‚ùå Error: Failed to clone repository\"\n    echo \"Please check your internet connection and GitHub access\"\n    exit 1\nfi\n\ncd \"$TEMP_DIR\"\n\necho \"üóÇÔ∏è Current files before cleanup:\"\nls -la *.md | wc -l\necho \" markdown files found\"\necho \"\"\n\necho \"üßπ Removing duplicate documentation files...\"\n# Action 01 files (moved to docs/action-01/)\nrm -f ACTION_01_IMPLEMENTATION.md\nrm -f ACTION_01_STATUS.md  \nrm -f ACTION_01_TESTING.md\necho \"   ‚úÖ Action 01 files removed\"\n\n# Action 02 files (moved to docs/action-02/)\nrm -f ACTION_02_PLAN.md\nrm -f ACTION_02_DELIVERY.md\nrm -f ACTION_02_STATUS.md\nrm -f ACTION_02_TESTING_FIXES.md\necho \"   ‚úÖ Action 02 files removed\"\n\n# Changelog files (moved to docs/action-02/)\nrm -f CHANGELOG-BUILDING-SYSTEM-FIXED.md\nrm -f CHANGELOG-CRITICAL-FIXES.md\nrm -f CHANGELOG-FINAL-FIXES.md\nrm -f CHANGELOG-OPCION-A-GALCON.md\necho \"   ‚úÖ Changelog files removed\"\n\n# Planning files (moved to docs/planning/)\nrm -f CLASSIC_EVOLUTION_PLAN.md\necho \"   ‚úÖ Planning files removed\"\n\n# Legacy files\nrm -f DELIVERY.md\nrm -f PROGRESS.md\necho \"   ‚úÖ Legacy files removed\"\n\necho \"üóëÔ∏è Removing deprecated files...\"\n# All DEPRECATED_ files\nrm -f DEPRECATED_*.md\nrm -f DEPRECATED_*.js\necho \"   ‚úÖ All deprecated files removed\"\n\necho \"üìã Removing cleanup instruction files...\"\n# Cleanup files\nrm -f CLEANUP_INSTRUCTIONS.md\nrm -f FINAL_CLEANUP.md\nrm -f NEW_README.md\nrm -f ARCHIVAL_COMPLETE.md\nrm -f CLEANUP_STATUS.md\necho \"   ‚úÖ Cleanup files removed\"\n\necho \"\"\necho \"üìä Files remaining after cleanup:\"\nls -la *.md\necho \"\"\n\necho \"üíæ Committing changes to GitHub...\"\ngit add -A\ngit commit -m \"CLEANUP: Remove all duplicate and obsolete files - automated cleanup\n\n- Removed Action 01 files (moved to docs/action-01/)\n- Removed Action 02 files (moved to docs/action-02/)\n- Removed changelog files (moved to docs/action-02/)\n- Removed planning files (moved to docs/planning/)\n- Removed all deprecated files\n- Removed cleanup instruction files\n\nResult: Clean project structure ready for energy fuel system development.\"\n\nif [ $? -eq 0 ]; then\n    echo \"‚úÖ Changes committed successfully\"\n    \n    echo \"üöÄ Pushing to GitHub...\"\n    git push origin action-02-balance-experiments\n    \n    if [ $? -eq 0 ]; then\n        echo \"‚úÖ Changes pushed to GitHub successfully!\"\n        echo \"\"\n        echo \"üéØ CLEANUP COMPLETE!\"\n        echo \"===================\"\n        echo \"‚úÖ Project structure is now clean and organized\"\n        echo \"‚úÖ All duplicate files removed\"\n        echo \"‚úÖ Documentation properly organized in docs/\"\n        echo \"‚úÖ Ready for energy fuel system development\"\n        echo \"\"\n        echo \"üìÅ Final project structure:\"\n        echo \"spaceGame/\"\n        echo \"‚îú‚îÄ‚îÄ README.md (updated)\"\n        echo \"‚îú‚îÄ‚îÄ ACTION-02-COMPLETE-DOCUMENTATION.md (current status)\"\n        echo \"‚îú‚îÄ‚îÄ index.html (game)\"\n        echo \"‚îú‚îÄ‚îÄ css/ (stylesheets)\"\n        echo \"‚îú‚îÄ‚îÄ js/ (game code)\"\n        echo \"‚îú‚îÄ‚îÄ docs/ (organized documentation)\"\n        echo \"‚îî‚îÄ‚îÄ src/ (future structure)\"\n    else\n        echo \"‚ùå Error pushing to GitHub\"\n        echo \"Please check your GitHub permissions\"\n    fi\nelse\n    echo \"‚ùå Error committing changes\"\nfi\n\necho \"\"\necho \"üßπ Cleaning up temporary directory...\"\ncd ..\nrm -rf \"$TEMP_DIR\"\necho \"‚úÖ Temporary files cleaned up\"\n\necho \"\"\necho \"üéâ CLEANUP SCRIPT COMPLETED!\"\necho \"The project is now ready for development.\"\necho \"\"\necho \"Next steps:\"\necho \"1. Clone the clean project: git clone -b action-02-balance-experiments https://github.com/ServH/spaceGame.git\"\necho \"2. Start energy fuel system development\"\necho \"\""